function stats_salida = funcion_recolector_stats(a1,r1,s1, a2,r2,s2, a3,r3,s3)\n% Recolecta estadisticas\n\npersistent stats;\n\nif isempty(stats)\n    stats = zeros(12,1);\nend\n\nstats(1) = stats(1) + a1 + a2 + a3;\nstats(2) = stats(2) + r1 + r2 + r3;\nstats(3) = stats(3) + s1 + s2 + s3;\n\ntotal = stats(1) + stats(2);\nif total > 0\n    tasa_exito = stats(1) / total;\n    tasa_slow = stats(3) / total;\nelse\n    tasa_exito = 0;\n    tasa_slow = 0;\nend\n\nstats_salida = [tasa_exito; tasa_slow; stats(1)];\n