function [BW_disponible, es_movil] = funcion_asignar_cliente(u_tipo, u_bw, prob_movil)\n% Asigna ancho de banda y tipo de cliente\n\nmedia_BW_desktop = 150;\nstd_BW_desktop = 30;\nmedia_BW_movil = 80;\nstd_BW_movil = 25;\n\nif u_tipo < prob_movil\n    es_movil = 1;\n    media = media_BW_movil;\n    std_dev = std_BW_movil;\nelse\n    es_movil = 0;\n    media = media_BW_desktop;\n    std_dev = std_BW_desktop;\nend\n\nz = sqrt(-2*log(u_bw)) * cos(2*pi*u_bw);\nBW_disponible = media + std_dev * z;\nBW_disponible = max(BW_disponible, 32);\n